:4:"link";a:1:{i:0;a:5:{s:4:"data";s:96:"http://en.wp.obenland.it/the-bootstrap/#utm_source=rss&utm_medium=rss&utm_campaign=the-bootstrap";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:8:"comments";a:1:{i:0;a:5:{s:4:"data";s:48:"http://en.wp.obenland.it/the-bootstrap/#comments";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:7:"pubDate";a:1:{i:0;a:5:{s:4:"data";s:31:"Sat, 03 Mar 2012 17:00:18 +0000";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:8:"category";a:2:{i:0;a:5:{s:4:"data";s:8:"Featured";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}i:1;a:5:{s:4:"data";s:7:"Website";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:31:"http://en.wp.obenland.it/?p=270";s:7:"attribs";a:1:{s:0:"";a:1:{s:11:"isPermaLink";s:5:"false";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:242:"The Bootstrap is a clean, friendly, modern WordPress Theme, with a great image and gallery handling, based on Bootstrap from Twitter. <a href="http://en.wp.obenland.it/the-bootstrap/">Continue reading <span class="meta-nav">&#8594;</span></a>";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:19:"Konstantin Obenland";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:40:"http://purl.org/rss/1.0/modules/content/";a:1:{s:7:"encoded";a:1:{i:0;a:5:{s:4:"data";s:4166:"<figure id="attachment_306" class="wp-caption alignleft" style="width: 312px;">\n					<a href="http://en.wp.obenland.it/the-bootstrap/the-bootstrap-wordpress-screenshot/" rel="attachment wp-att-306"><img class="size-full wp-image-306 " title="The Bootstrap" src="http://en.wp.obenland.it/files/2012/03/the-bootstrap-wordpress-screenshot.png" alt="Screenshot of The Bootstrap WordPress Theme" width="300" height="225" /></a>\n					<figcaption class="wp-caption-text">The Bootstrap in all it&#39;s glory.</figcaption>\n				</figure>\n<p>The Bootstrap is a clean, friendly, modern WordPress Theme, with a great image and gallery handling, based on <a title="Bootstrap on Github" href="http://twitter.github.com/bootstrap/" target="_blank">Bootstrap from Twitter</a>.</p>\n<p>It is the first Theme I submitted to <a title="To the WordPress Theme Repository" href="http://wordpress.org/extend/themes/the-bootstrap" target="_blank">the WordPress Theme Repository</a>.</p>\n<p><a style="background: #222222; border-radius: 3px 3px 3px 3px; box-shadow: 0 1px 2px rgba(0, 0, 0, 0.3); color: #eeeeee; cursor: pointer; font-size: 15px; margin: 20px 0; padding: 5px 42px 5px 22px; text-shadow: 0 -1px 0 rgba(0, 0, 0, 0.3);" title="The Bootstrap" href="http://the-bootstrap.obenland.it" target="_blank">See it in action ?</a></p>\n<p><a style="background: #222222; border-radius: 3px 3px 3px 3px; box-shadow: 0 1px 2px rgba(0, 0, 0, 0.3); color: #eeeeee; cursor: pointer; font-size: 15px; margin: 20px 0; padding: 5px 42px 5px 22px; text-shadow: 0 -1px 0 rgba(0, 0, 0, 0.3);" title="Please use the issue tracker for support" href="https://github.com/obenland/The-Bootstrap/issues" target="_blank">Find support ?</a><span id="more-270"></span></p>\n<h2>Responsive Layout</h2>\n<p><strong></strong>The Bootstrap benefits from <a title="More on the responsive grid" href="http://twitter.github.com/bootstrap/scaffolding.html#responsive" target="_blank">the responsive grid layout</a>, that Bootstrap has to offer. So no matter from where your site is accessed, it will always display neatly on any device!</p>\n<h2>Header images with flexible heights</h2>\n<p>With v1.2.1, The Bootstrap supports header images with flexible heights, which [will be] introduced in WordPress 3.4. This gives you even more flexibility, when it comes to image headers and displaying your site name.</p>\n<h2>Navigation Bar</h2>\n<p>Other than WordPress users know their navigation bars, Bootstrap uses the parent element as a title for child elements, if there are any. Therefore the parent element can not be directly accessed through the navigation in this Theme.</p>\n<h2>Image carousel on Gallery Post Formats</h2>\n<p>With v1.2.3, the <a title="See an example" href="http://the-bootstrap.obenland.it/#post-555" target="_blank">first ten images of a gallery will be shown in a slick image slider</a> on overview pages, when the post is marked with the gallery post format. You can even implement you own carousel within posts or pages, when following <a title="Carousels from Bootstrap" href="http://twitter.github.com/bootstrap/javascript.html#carousel" target="_blank">the markup examples</a> provided at the Bootstrap documentation!</p>\n<h2>License</h2>\n<p>The Bootsrap is released under the GNU license (like the rest of my WordPress stuff), so feel free to break it, tweak it and customize it to fit your personal needs!</p>\n<h2>Contributions</h2>\n<p>There are several ways to contribute to this project. While everything I publish on WordPress.org is free, including this Theme of course, <a title="Donate to The Bootstrap" href="https://www.paypal.com/cgi-bin/webscr?cmd=_s-xclick&amp;hosted_button_id=542W6XT4PLT4L" target="_blank">contributions through Donations</a> are always welcome. They let me spend more time on maintaining The Bootstrap and add new features as they become available.</p>\n<p>If you are developer and you have a great idea for The Bootstrap, feel free to <a title="The Bootstrap on GitHub" href="https://github.com/obenland/The-Bootstrap" target="_blank">submit a pull request or open an issue on GitHub</a>. You are also more than welcome to fork it and make it your own!</p>\n";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:36:"http://wellformedweb.org/CommentAPI/";a:1:{s:10:"commentRss";a:1:{i:0;a:5:{s:4:"data";s:44:"http://en.wp.obenland.it/the-bootstrap/feed/";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:38:"http://purl.org/rss/1.0/modules/slash/";a:1:{s:8:"comments";a:1:{i:0;a:5:{s:4:"data";s:2:"52";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}}}s:27:"http://www.w3.org/2005/Atom";a:1:{s:4:"link";a:1:{i:0;a:5:{s:4:"data";s:0:"";s:7:"attribs";a:1:{s:0:"";a:3:{s:4:"href";s:30:"http://en.wp.obenland.it/feed/";s:3:"rel";s:4:"self";s:4:"type";s:19:"application/rss+xml";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:44:"http://purl.org/rss/1.0/modules/syndication/";a:2:{s:12:"updatePeriod";a:1:{i:0;a:5:{s:4:"data";s:6:"hourly";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:15:"updateFrequency";a:1:{i:0;a:5:{s:4:"data";s:1:"1";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:28:"http://www.w3.org/1999/xhtml";a:1:{s:4:"meta";a:1:{i:0;a:5:{s:4:"data";s:0:"";s:7:"attribs";a:1:{s:0:"";a:2:{s:4:"name";s:6:"robots";s:7:"content";s:7:"noindex";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}}}}}}}}s:4:"type";i:128;s:7:"headers";a:9:{s:4:"date";s:29:"Mon, 21 May 2012 13:59:32 GMT";s:6:"server";s:6:"Apache";s:12:"x-powered-by";s:15:"PHP/5.3.13-nmm1";s:10:"x-pingback";s:35:"http://en.wp.obenland.it/xmlrpc.php";s:4:"etag";s:34:""23ac481db8e8010117f0d97a37d226cd"";s:13:"last-modified";s:29:"Tue, 15 May 2012 10:25:12 GMT";s:4:"vary";s:15:"Accept-Encoding";s:10:"connection";s:5:"close";s:12:"content-type";s:23:"text/xml; charset=UTF-8";}s:5:"build";s:14:"20090627192103";}', 'no'),
